return items.flatMap(item => {
  const root = item.json.output ? item.json.output : [item.json];   // unwrap "output" array if present

  return root.map(data => {
    const description = (data.description || "").toString();
    const meta = (data.meta || "").toString();

    let keywords = data.keywords || data.keywords_used || [];
    if (!Array.isArray(keywords)) {
      keywords = keywords.toString().split(",").map(k => k.trim()).filter(Boolean);
    }

    if (!keywords.length) {
      return {
        json: {
          ...data,
          seo_score: 0,
          audit: { word_count: 0, keywords_used: [], keywords_missing: [], keyword_coverage: "0%" },
          note: "‚ö†Ô∏è No keywords provided ‚Äî check data path."
        }
      };
    }

    const descLower = description.toLowerCase();
    const metaLower = meta.toLowerCase();

    const used = [];
    const missing = [];

    keywords.forEach(keyword => {
      const key = keyword.toLowerCase().trim();
      if (descLower.includes(key) || metaLower.includes(key)) used.push(keyword);
      else missing.push(keyword);
    });

    const coverage = used.length / keywords.length;
    const wordCount = description.split(/\s+/).filter(Boolean).length;

    let lengthScore = 0;
    if (wordCount >= 100 && wordCount <= 200) lengthScore = 30;
    else if (wordCount >= 80 && wordCount <= 250) lengthScore = 20;
    else lengthScore = 10;

    const keywordScore = coverage * 70;
    const seoScore = Math.round(keywordScore + lengthScore);

    return {
      json: {
        ...data,
        seo_score: seoScore,
        audit: {
          word_count: wordCount,
          keywords_used: used,
          keywords_missing: missing,
          keyword_coverage: `${Math.round(coverage * 100)}%`
        }
      }
    };
  });
});

return items.map(item => {
  // Extract and clean up fields
  const sku = item.json["SKU"] || "";
  const name = item.json["Product Name"] || "";
  const specs = item.json["Specs"] || "";
  const features = item.json["Features"] || "";
  const material = item.json["Material"] || "";
  const price = item.json["Price"] || "";
  const targetUrl = item.json["Target URL"] || "";
  const tone = item.json["Style Guide"] || "Friendly";

  // Split keywords string into a clean array
  const rawKeywords = item.json["Target Keywords"] || "";
  const keywords = rawKeywords
    .split(",")
    .map(k => k.trim())
    .filter(k => k.length > 0);

  // Combine product data into one formatted string for AI context
  const productData = `
  Product Name: ${name}
  Specifications: ${specs}
  Features: ${features}
  Material: ${material}
  Price: $${price}
  URL: ${targetUrl}
  `;

  return {
    json: {
      sku,
      name,
      tone,
      keywords,
      product_data: productData.trim(),
      target_url: targetUrl
    }
  };
});

