return items.map(item => {
  const orgs = item.json.organizations;
  const hasOrg = Array.isArray(orgs) && orgs.length > 0;
  const org = hasOrg ? orgs[0] : null;

  const breadcrumbs = item.json.breadcrumbs || [];
  const sizeBreadcrumb = breadcrumbs.find(b => b.label === '# Employees');

  const industry = org?.industry || 'Unknown';
  const size = org?.organization_num_employees || (sizeBreadcrumb ? sizeBreadcrumb.value : null);
  const revenue = org?.organization_revenue_printed || org?.organization_revenue || 'Unknown';
  const phone = org?.sanitized_phone || [];

  const isQualified = (typeof size === 'number' ? size >= 50 : true);

  return {
    json: {
      companyName: org?.name || 'Organization could not be found',
      industry: 'Saas',
      companySize: size,
      revenue,
      keyContacts: phone,
      qualificationStatus: isQualified ? 'Qualified' : 'Not Qualified'
    }
  };
});

return items.map(item => {
