// Access the full JSON from previous node
const data = $json; 

// Extract the people array from the JSON object
const people = data.people;

// Define seniority ranking (lower number = higher seniority)
const seniorityRank = {
  "c_level": 1,
  "vp": 2,
  "director": 3,
  "manager": 4,
 "senior": 5,
  "staff": 6,
  "entry": 7,
  "intern": 8
};

// Sort people by seniority rank
people.sort((a, b) => {
  const aSeniority = a.seniority?.toLowerCase();
  const bSeniority = b.seniority?.toLowerCase();

  const aRank = seniorityRank[aSeniority];
  const bRank = seniorityRank[bSeniority];

  // If both are in the ranking list, sort by rank
  if (aRank !== undefined && bRank !== undefined) {
    return aRank - bRank;
  }

  // If a is ranked but b is not, a goes first
  if (aRank !== undefined && bRank === undefined) {
    // b is unknown but not null, so a before b
    // unless b is null or empty string
    if (bSeniority === null || bSeniority === undefined || bSeniority === "") {
      return -1; // a before b
    }
    return -1; // a before b
  }

  // If b is ranked but a is not, b goes first
  if (aRank === undefined && bRank !== undefined) {
    if (aSeniority === null || aSeniority === undefined || aSeniority === "") {
      return 1; // b before a
    }
    return 1; // b before a
  }

  // Both not ranked, check for null or empty
  const aIsNull = (aSeniority === null || aSeniority === undefined || aSeniority === "");
  const bIsNull = (bSeniority === null || bSeniority === undefined || bSeniority === "");

  if (aIsNull && !bIsNull) {
    return 1; // b before a
  }
  if (!aIsNull && bIsNull) {
    return -1; // a before b
  }
  if (aIsNull && bIsNull) {
    return 0; // equal
  }

  // Both unknown but not null/empty - sort alphabetically
  return aSeniority.localeCompare(bSeniority);
});

// Return the sorted people as separate items for n8n
return people.map(person => {
  return {
    json: person
  };
});
